console.log(`\n🎬 Importing 200 NEW movies from JustWatch Iraq (English titles + pictures)\n`);
console.log(`📊 Current database: ${movies.length} movies\n`);

const existingTitles = new Set(movies.map(m => m.title.toLowerCase()));
let added = 0;
let skipped = 0;

// Filter out existing movies and get first 200 new ones
const newMovies = moviesToAdd.filter(m => !existingTitles.has(m.title.toLowerCase())).slice(0, 200);

if (newMovies.length < 200) {
    console.log(`⚠️  Only found ${newMovies.length} new movies from the list. Need ${200 - newMovies.length} more.\n`);
}

for (const movieInfo of newMovies) {
    const newMovie = {
        id: generateId(movieInfo.title, movieInfo.year),
        title: movieInfo.title, // English title
        originalTitle: movieInfo.title,
        year: movieInfo.year,
        runtimeMinutes: null,
        description: `Watch ${movieInfo.title} (${movieInfo.year}) online on JustWatch Iraq. ${movieInfo.genres.join(', ')} film.`,
        thumbnailUrl: movieInfo.poster,
        posterUrl: movieInfo.poster, // Real TMDB poster
        hlsUrl: null, // Need YouTube link
        youtubeEmbed: false,
        language: 'EN',
        genres: movieInfo.genres,
        tags: movieInfo.genres.map(g => g.toLowerCase()),
        isFeatured: false,
        director: movieInfo.director || null,
        cast: movieInfo.cast || null,
        source: {
            name: "JustWatch Iraq",
            url: `https://www.justwatch.com/iq/movie/${movieInfo.title.toLowerCase().replace(/\s+/g, '-')}`
        },
        license: {
            type: null,
            url: null
        },
        createdAt: new Date().toISOString(),
        needsYouTubeLink: true,
        importedFromJustWatch: true
    };

    movies.push(newMovie);
    existingTitles.add(movieInfo.title.toLowerCase());
    added++;
    
    if (added % 20 === 0) {
        console.log(`✅ Added ${added} movies so far...`);
    }
}

fs.writeFileSync(moviesPath, JSON.stringify(movies, null, 4), 'utf8');

console.log(`\n✅ Import completed!`);
console.log(`   Added: ${added} NEW movies with English titles + real posters`);
console.log(`   Skipped: ${skipped} movies (already exist)`);
console.log(`   Total in database: ${movies.length}\n`);
